import React from 'react';
import TronLinkGuide from 'components/TronLinkGuide';
import TronWeb from 'tronweb';
import Utils from 'utils';
import Swal from 'sweetalert2';

import './App.scss';

const FOUNDATION_ADDRESS = 'TWiWt5SEDzaEqS6kE5gandWMNfxR2B5xzg';

class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            balance: 0,
            inputValue: '',

              tronWeb: {
                  installed: false,
                  loggedIn: false
              },
            }
        this.updateInputValue = this.updateInputValue.bind(this)

    }

    async componentDidMount() {

        this.setState({loading:true})
        await new Promise(resolve => {
            const tronWebState = {
                installed: !!window.tronWeb,
                loggedIn: window.tronWeb && window.tronWeb.ready
            };

            if(tronWebState.installed) {
                this.setState({
                    tronWeb:
                    tronWebState
                });

                return resolve();
            }

            let tries = 0;

            const timer = setInterval(() => {
                if(tries >= 10) {
                    const TRONGRID_API = 'https://api.trongrid.io';

                    window.tronWeb = new TronWeb(
                        TRONGRID_API,
                        TRONGRID_API,
                        TRONGRID_API
                    );

                    this.setState({
                        tronWeb: {
                            installed: false,
                            loggedIn: false
                        }
                    });

                    clearInterval(timer);
                    return resolve();
                }

                tronWebState.installed = !!window.tronWeb;
                tronWebState.loggedIn = window.tronWeb && window.tronWeb.ready;

                if(!tronWebState.installed)
                    return tries++;

                this.setState({
                    tronWeb: tronWebState
                });

                resolve();
            }, 100);
        });

        if(!this.state.tronWeb.loggedIn) {
            // Set default address (foundation address) used for contract calls
            // Directly overwrites the address object as TronLink disabled the
            // function call
            window.tronWeb.defaultAddress = {
                hex: window.tronWeb.address.toHex(FOUNDATION_ADDRESS),
                base58: FOUNDATION_ADDRESS
            };

            window.tronWeb.on('addressChanged', () => {
                if(this.state.tronWeb.loggedIn)
                    return;

                this.setState({
                    tronWeb: {
                        installed: true,
                        loggedIn: true
                    }
                });
            });
        }
        await Utils.setTronWeb(window.tronWeb);

    }

    async getBalance(_address){
        const balance = (await Utils.contract.balanceOf(_address).call()).toNumber();
        //const balance = await Utils.contract.decimals().call();

        console.log('balance', balance);

            this.setState({balance:balance})

        }


    Transfer(_to, _amount){

        Utils.contract.transfer(_to, _amount).send({
            shouldPollResponse: true,
            callValue: 0
        }).then(res => Swal({
            title:'Vote Casted',
            type: 'success'
        })).catch(err => Swal({
            title:'Vote Failed',
            type: 'error'

        }));

    }

    updateInputValue (evt) {
    console.log('inputValue : ', this.state.inputValue);
        this.setState({
          inputValue: evt.target.value
        });
    }

    render() {
        if(!this.state.tronWeb.installed)
            return <TronLinkGuide />;

        if(!this.state.tronWeb.loggedIn)
            return <TronLinkGuide installed />;

        return (
              <div className='row'>
                <div className='col-lg-12 text-center' >
                  <h1>DAPP Template</h1>
                  <br/>
                  <input style={{ width:"400px" }} value={this.state.inputValue} onChange={this.updateInputValue}/>
                  <br/>
                  <br/>
                  <button className='btn btn-primary' onClick={(event) => {
                                                                       event.preventDefault()
                                                                       this.getBalance(balance)
                                                                     }  }>Get Balance
                  </button>
                  <br/>
                  <br/>
                  <p>Your balance is : {this.state.balance}</p>
                  <br/>
                  <br/>
                  <hr/>

                  <button className='btn btn-primary' onClick={(event) => {
                                                                       event.preventDefault()
                                                                       this.Transfer(100)
                                                                     }  }>Transfer
                  </button>
                </div>
              </div>
        );
    }
}

export default App;
