import React from 'react';
import TronLinkGuide from 'components/TronLinkGuide';
import TronWeb from 'tronweb';
import Utils from 'utils';
import Swal from 'sweetalert2';


import './App.scss';

const FOUNDATION_ADDRESS = 'TWiWt5SEDzaEqS6kE5gandWMNfxR2B5xzg';

////////////////////////////////////////////////////////////////////////////////////
const contractAddress = 'TXqE5hfdRo7SfDi1VCMQz4TdXVoTzch6NB';   /// Add your contract address here
////////////////////////////////////////////////////////////////////////////////////
var scores, activePlayer, cube, currentClass;

class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            tronwebaddress:'',
            score0:'',
            score1:'',

              tronWeb: {
                  installed: false,
                  loggedIn: false
              },
            }
        this.changeSide = this.changeSide.bind(this)
        this.init = this.init.bind(this)

    }

    async componentDidMount() {

        this.setState({loading:true})
        await new Promise(resolve => {
            const tronWebState = {
                installed: !!window.tronWeb,
                loggedIn: window.tronWeb && window.tronWeb.ready
            };

            if(tronWebState.installed) {
                this.setState({
                    tronWeb:
                    tronWebState
                });

                return resolve();
            }

            let tries = 0;

            const timer = setInterval(() => {
                if(tries >= 10) {
                    const TRONGRID_API = 'https://api.trongrid.io';

                    window.tronWeb = new TronWeb(
                        TRONGRID_API,
                        TRONGRID_API,
                        TRONGRID_API
                    );

                    this.setState({
                        tronWeb: {
                            installed: false,
                            loggedIn: false
                        }
                    });

                    clearInterval(timer);
                    return resolve();
                }

                tronWebState.installed = !!window.tronWeb;
                tronWebState.loggedIn = window.tronWeb && window.tronWeb.ready;

                if(!tronWebState.installed)
                    return tries++;

                this.setState({
                    tronWeb: tronWebState
                });

                resolve();
            }, 100);
        });

        if(!this.state.tronWeb.loggedIn) {
            // Set default address (foundation address) used for contract calls
            // Directly overwrites the address object as TronLink disabled the
            // function call
            window.tronWeb.defaultAddress = {
                hex: window.tronWeb.address.toHex(FOUNDATION_ADDRESS),
                base58: FOUNDATION_ADDRESS
            };

            window.tronWeb.on('addressChanged', () => {
                if(this.state.tronWeb.loggedIn)
                    return;

                this.setState({
                    tronWeb: {
                        installed: true,
                        loggedIn: true
                    }
                });
            });
        }

        //this.init();
        await Utils.setTronWeb(window.tronWeb, contractAddress);
        //const tmp_name = await Utils.contract.name().call();
        const tmp_tronwebaddress = Utils.tronWeb.address.fromHex((((await Utils.tronWeb.trx.getAccount()).address).toString()))
        await this.setState({
          tronwebaddress:tmp_tronwebaddress
        });

        //await Utils.setTronWeb(window.tronWeb);
        //console.log(Utils.tronWeb.address.fromHex((((await Utils.tronWeb.trx.getAccount()).address).toString())));  /////// Get account address and info
       // console.log(await Utils.tronWeb.trx.getBalance());
    }





    //init();

    async changeSide() {
      var dice = (Math.floor(Math.random()*6)+1);
      console.log("dice : ", dice);
      var showClass = 'show-' + dice;
      if (currentClass) {
        cube.classList.remove(currentClass);
      }
      cube.classList.add(showClass);
      currentClass = showClass;
      //function updateScore() {
      //document.querySelector('.player-controls').classList.remove('disable-controls');
      //document.querySelector('.fa-dice').classList.remove('grey');

      //document.querySelector('#score-' + activePlayer).textContent = dice;

      if(activePlayer == 0){
          await this.setState({
            score0 : dice
          });
      }

      if(activePlayer == 1){
          await this.setState({
            score1 : dice
          });
          document.getElementById("roll-button").style.display = "none";

          if(this.state.score0 > this.state.score1){
                Swal({
                    title:'Player 1 Won!!',
                    type: 'success'
                });
          }

          if(this.state.score0 < this.state.score1){
                Swal({
                    title:'Player 2 Won!!',
                    type: 'success'
                });
          }

          if(this.state.score0 == this.state.score1){
                Swal({
                    title:'Match Draw!!',
                    type: 'success'
                });
          }

      }



      console.log('score0', this.state.score0);
      console.log('score1', this.state.score1);

      this.nextPlayer();

      //}
      //updateScore();
      //setTimeout(this.updateScore, 2000);


    }

    async roll() {
      cube.classList.remove('animation-1');
      var counter = 0;
      var loopCount = 30;
      function spinCube() {

        if (++counter >= loopCount) {
            clearInterval(interval);
            cube.classList.remove('animation-2');
            console.log("asdfasdfasdf");

        } else {
            cube.classList.add('animation-2');
            //document.querySelector('.player-controls').classList.remove('disable-controls');
            //document.querySelector('.fa-dice').classList.remove('grey');

            console.log("QQQQQQQQQQQQ");

        }
      }
      var interval = setInterval(spinCube, 60);

      this.changeSide();

      //document.querySelector('#score-' + activePlayer).textContent = scores[activePlayer];
        //this.nextPlayer();

    };

    nextPlayer() {
      console.log("activePlayer : ", activePlayer);
      activePlayer === 0 ? activePlayer = 1 : activePlayer = 0;
      document.querySelector('.player-0-panel').classList.toggle('active');
      document.querySelector('.player-1-panel').classList.toggle('active');
      document.querySelector('.fa-dice').classList.toggle('fa-flip-horizontal');
    }

    //document.querySelector('.btn-new').addEventListener('click', init);

    init() {
    console.log("init()");
      scores = [0,0];
      activePlayer = 0;
      cube = document.querySelector('.cube');

      this.setState({score0: 0});
      this.setState({score1: 0});
      document.getElementById('name-0').textContent = 'Player1';
      document.getElementById('name-1').textContent = 'Player2';
      //document.querySelector('.player-0-panel').classList.remove('winner');
      //document.querySelector('.player-1-panel').classList.remove('winner');
      document.querySelector('.player-0-panel').classList.remove('active');
      document.querySelector('.player-1-panel').classList.remove('active');
      document.querySelector('.player-0-panel').classList.add('active');
      //document.querySelector('.player-controls').classList.remove('player-controls-right');
      document.querySelector('.dice').classList.remove('displaynone');
      //document.querySelector('.player-controls').classList.remove('displaynone');
      cube.classList.add('animation-1');
      document.getElementById("dice-game").style.display = "block";
      document.getElementById("roll-button").style.display = "block";




    }



    render() {
        if(!this.state.tronWeb.installed)
            return <TronLinkGuide />;

        if(!this.state.tronWeb.loggedIn)
            return <TronLinkGuide installed />;

        return (
              <div className='row'>
                <div className='col-lg-12 text-center' >
                  <hr/>

                      <div className="topnav">
                        <img src={'CodeXpert.png'} width="200"/>
                      </div>
                  <hr style={{color: 'white', backgroundColor: 'white', height: 0.5}}/>

                  <h1 className="topnav" style={{color : 'red' }}>TRON TRC20 TOKEN DICE GAME</h1>
